import React, { useEffect, useRef, useState } from "react";
import styles from "./css/Detail.module.css";
import { useNavigate, useHistory, useParams } from "react-router-dom";
import axios from "axios";

const Detail = () => {
  const { gender, cate, id } = useParams();
  const [data, setData] = useState([]);
  const [count, setCount] = useState(1);
  const [size, setSize] = useState("");
  const navigate = useNavigate();
  // let data = [];

  useEffect(() => {
    axios
      .get(
        `https://raquim47.github.io/data/cozy/json/${gender}_${cate}.json`
      )
      .then((res) => setData(res.data.find((item) => item.id === parseInt(id))));

    }, []);
    console.log(data);


  const increaseCount = () => () => {
    count ? setCount((prev) => parseInt(prev) + 1) : setCount(1);
  };
  const decreaseCount = () => () => {
    count > 1 && setCount((prev) => parseInt(prev) - 1);
  };

  const onSubmitBtn = (e) => {
    e.preventDefault();
    if (!size || count < 1) {
      alert("올바른 수량과 사이즈를 선택해주세요");
      return;
    }
    navigate("/cart");
  };

  return (
    <div className={styles.detail}>
      {1 === 2 && <div className={styles.detail__inner}>
        <div className={styles.detail__back}>←</div>
        <section className={styles.detail__left}>
          <img src={data.url}></img>
        </section>
        <section className={styles.detail__right}>
          <form className={styles.order_form}>
            <h3>{data.title}</h3>
            {data.size && (
              <div className={`${styles.order_form__option} ${styles.size}`}>
                <h4>size</h4>
                <ul>
                  {data.size &&
                    data.size.map((sizeItem) => (
                      <li
                        onClick={() => setSize(sizeItem)}
                        className={size === sizeItem && styles.active}
                        key={sizeItem}
                      >
                        {sizeItem}
                      </li>
                    ))}
                </ul>
              </div>
            )}
            <div className={styles.order_form__option}>
              <h4>count</h4>
              <div className={styles.count_input}>
                <input
                  type="text"
                  value={count}
                  onChange={(e) => {
                    setCount(e.target.value);
                  }}
                />
                <div className={styles.count_input__btn}>
                  <div
                    onClick={increaseCount()}
                    className={styles.count_input__up}
                  ></div>
                  <div
                    onClick={decreaseCount()}
                    className={styles.count_input__down}
                  ></div>
                </div>
              </div>
            </div>
            <div className={`${styles.order_form__option} ${styles.price}`}>
              <h4>price</h4>
              <span>$ {count > 0 && data.price * count}</span>
            </div>
            <button
              type="submit"
              onClick={(e) => onSubmitBtn(e)}
              className={styles.order_form__submit_btn}
            >
              Add Cart
            </button>
          </form>
        </section>
      </div>}
    </div>
  );
};

export default Detail;
