import React, { useEffect, useRef, useState } from "react";
import styles from "./Banner.module.css";

const Banner = () => {
  const [active, setActive] = useState("");

  const leftBg = useRef();
  useEffect(() => {
    setActive("styles.active");
  }, []);
  return (
    <div className={styles.banner}>
      <div className={styles.banner_left}>
        <div
          className={styles.banner_left__bg + " " + active}
          ref={leftBg}
        ></div>
        <div className={styles.banner_left__img}>
          <img src={process.env.PUBLIC_URL + "img/banner01.jpg"} />
        </div>
        <div className={styles.banner_left__textbox}>
          <h2>
            Actuality
            <br />
            Trends
            <br />
            <small>Cozy Fall 2022</small>
          </h2>
          <div className={styles.banner_left__desc}>
            <span>
              The Cozy Architectural collection is everything a businesswoman
              could wish for. It knows no trends or seasons, making it a sleek,
              refined, and consistent selection of modern items that are made to
              stand the test of time.
            </span>
          </div>
        </div>
      </div>
      <div className={styles.banner_right}>
        <img src={process.env.PUBLIC_URL + "img/banner02.jpg"} />
        <img src={process.env.PUBLIC_URL + "img/banner03.jpg"} />
        <img src={process.env.PUBLIC_URL + "img/banner04.jpg"} />
      </div>
    </div>
  );
};
export default Banner;
