import React, { useEffect, useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faBagShopping,
  // faMagnifyingGlass,
} from "@fortawesome/free-solid-svg-icons";
import styles from "./Nav.module.css";

const Nav = () => {
  const navigate = useNavigate();
  const navItem = ["Costume", "Shoes", "Accessories", "Perfume"];
  const [navIndex, setNavIndex] = useState();
  let menu = useRef();
  let hamburger = useRef();

  function click(e) {
    e.classList.toggle(`${styles.active}`);
    menu.current.classList.toggle(`${styles.active}`);
  }
  function close() {
    hamburger.current.classList.remove(`${styles.active}`);
    menu.current.classList.remove(`${styles.active}`);
  }
  return (
    <>
      <nav className={styles.nav}>
        <div className={styles.container}>
          <div className={styles.menu} ref={menu}>
            <h1 className={styles.logo}>Cozy</h1>
            <ul>
              {navItem.map((item, i) => {
                return (
                  <li
                    onClick={() => {
                      setNavIndex(i);
                      close();
                    }}
                  ></li>
                );
              })}
              <li
                onClick={() => {
                  setNavIndex(0);
                  close();
                }}
                className={navIndex === 0 ? `${styles.active}` : null}
              >
                Costume
              </li>
              <li
                onClick={() => {
                  setNavIndex(1);
                  close();
                }}
                className={navIndex === 1 ? `${styles.active}` : null}
              >
                Shoes
              </li>
              <li
                onClick={() => {
                  setNavIndex(2);
                  close();
                }}
                className={navIndex === 2 ? `${styles.active}` : null}
              >
                Accessories
              </li>
              <li
                onClick={() => {
                  setNavIndex(3);
                  close();
                }}
                className={navIndex === 3 ? `${styles.active}` : null}
              >
                Perfume
              </li>
            </ul>
          </div>
          <div className={styles.user}>
            <ul>
              <li className={styles.login_btn}>Login</li>
              <li className={styles.cart_icon}>
                <FontAwesomeIcon icon={faBagShopping} />
              </li>
              <li
                className={styles.hamburger}
                onClick={(e) => {
                  click(e.currentTarget);
                }}
                ref={hamburger}
              >
                <span></span>
                <span></span>
                <span></span>
              </li>
            </ul>
          </div>
        </div>
      </nav>
    </>
  );
};
export default Nav;
