import React, { useEffect, useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faBagShopping,
  // faMagnifyingGlass,
} from "@fortawesome/free-solid-svg-icons";
import { useSelector } from "react-redux";
import styles from "./Nav.module.css";

const Nav = (props) => {
  const navigate = useNavigate();
  const navItem = ["men", "women", "accessories"];
  const [navIndex, setNavIndex] = useState();
  const menu = useRef();
  const hamburger = useRef();
  const user = useRef();

  // function userColor() {
  //   user.current.classList.add(`${styles.active}`);
  // }
  function click(e) {
    e.classList.toggle(`${styles.active}`);
    menu.current.classList.toggle(`${styles.active}`);
  }
  function close() {
    hamburger.current.classList.remove(`${styles.active}`);
    menu.current.classList.remove(`${styles.active}`);
  }

  return (
    <>
      <nav className={styles.nav}>
        <div className={styles.container}>
          <div className={styles.menu} ref={menu}>
            <h1
              className={styles.logo}
              onClick={() => {
                navigate("/");
                setNavIndex(100);
              }}
            >
              Cozy
            </h1>
            <ul>
              {/* 네비 버튼을 누르면
              어떤state가 true로 바뀌고
              어떤 state가 true로 바뀌면
              banner.js의 애니메이션 적용할 요소들에 .unmount 클래스를 추가한다.

               2초정도 쉬고 "/item"으로 이동한다 */}
              {navItem.map((item, i) => {
                return (
                  <li
                    key={(item, i)}
                    onClick={() => {
                      setNavIndex(i);
                      close();
                      props.setUnmount(true);
                      setTimeout(() => {
                        navigate(`/${item}`);
                      }, 4000);
                    }}
                    className={navIndex === i ? `${styles.active}` : null}
                  >
                    {item}
                  </li>
                );
              })}
            </ul>
          </div>
          <div className={styles.user}>
            <ul ref={user} className={`${styles[props.userColor]}`}>
              <li className={styles.login_btn}>Login</li>
              <li className={styles.cart_icon}>
                <FontAwesomeIcon icon={faBagShopping} />
              </li>
              <li
                className={styles.hamburger}
                onClick={(e) => {
                  click(e.currentTarget);
                }}
                ref={hamburger}
              >
                <span></span>
                <span></span>
                <span></span>
              </li>
            </ul>
          </div>
        </div>
      </nav>
    </>
  );
};
export default Nav;
