import React, { useEffect, useRef, useState } from "react";
import styles from "./css/Detail.module.css";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";

const Detail = ({ setPage }) => {
  const detailInfo = useSelector((state) => state.detailInfo);
  const [count, setCount] = useState(1);
  const navigate = useNavigate();
  useEffect(() => {
    if (!detailInfo.title) {
      navigate(`/men`);
    }
    setPage("");
  }, []);
  const increaseCount = () => () => {
    count ? setCount(prev => parseInt(prev) + 1) : setCount(1);
  }
  const decreaseCount = () => () => {
    count > 1 && setCount(prev => parseInt(prev) - 1);
  }
  return (
    <div className={styles.detail}>
      <section className={styles.detail__left}>
        <img src={detailInfo.url}></img>
      </section>
      <section className={styles.detail__right}>
        <form className={styles.order_form}>
          <h3>{detailInfo.title}</h3>
          {/* <div className={styles.order_form__price}>
            <h4>price</h4>
            <span>$ {detailInfo.price}</span>
          </div> */}
          <div className={styles.order_form__option}>
            <h4>size</h4>
            <ul>
              {detailInfo.size &&
                detailInfo.size.map((size) => <li>{size}</li>)}
            </ul>
          </div>
          <div className={styles.order_form__option}>
            <h4>count</h4>
            <div className={styles.count_input}>
              <input
                type="text"
                value={count}
                onChange={(e) => {
                  setCount(e.target.value);
                }}
              />
              <div className={styles.count_input__btn}>
                <div onClick={increaseCount()} className={styles.count_input__up}></div>
                <div onClick={decreaseCount()} className={styles.count_input__down}></div>
              </div>
            </div>
          </div>
          <div className={styles.order_form__option}>
            <h4>price</h4>
            <span>$ {detailInfo.price * count}</span>
          </div>
          <button type="submit" className={styles.order_form__submit_btn}>
            Add Cart
          </button>
        </form>
      </section>
    </div>
  );
};

export default Detail;
