import { useState } from 'react';
// css
import "./reset.css";
// route
import Main from "./router/Main";
import Men from "./router/Men";
// component
import Nav from "./components/Nav";

function App() {
  // const [navItemColor, setNavItemColor] = useState(0);
  const [userMenuColor,setUserMenuColor] = useState("");
  const [navIndex, setNavIndex] = useState("");
  console.log(navIndex)
  // 네비 : 유저메뉴컬러, 네비인덱스, 언마운트?
  return (
    <div className="App">
      <Nav userMenuColor={userMenuColor} navIndex={navIndex} setNavIndex={setNavIndex}/>
      <Routes>
        <Route path="/" element={<Main setUserMenuColor={setUserMenuColor} navIndex={navIndex} setNavIndex={setNavIndex}/>}></Route>
        <Route path="/men" element={<Men navIndex={navIndex} setNavIndex={setNavIndex}/>}></Route>
      </Routes>
    </div>
  );
}

export default App;
