import React, { useEffect, useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faBagShopping,
  faMagnifyingGlass,
} from "@fortawesome/free-solid-svg-icons";
import styles from "./Nav.module.css";

const Nav = () => {
  const navigate = useNavigate();
  const [navIndex, setNavIndex] = useState();
  function click(e) {
    e.classList.toggle(`${styles.active}`);
    // menu.current.classList.toggle(`${styles.active}`);
  }
  return (
    <>
      <nav className={styles.nav}>
        <div className={styles.container}>
          <div className={styles.nav__left}>
            <h1 className={styles.logo}>Cozy</h1>
            <ul>
              <li
                onClick={()=>{
                  console.log(navIndex)
                  setNavIndex(0)}}
                className={navIndex == 0 ? `${styles.active}` : null}
              >
                Costume
              </li>
              <li>Shoes</li>
              <li>Accessories</li>
              <li>Perfume</li>
            </ul>
          </div>
          <div className={styles.nav__right}>
            <ul>
              <li className={styles.login_btn}>Login</li>
              <li className={styles.cart_icon}>
                <FontAwesomeIcon icon={faBagShopping} />
              </li>
              <li
                className={styles.hamburger}
                onClick={(e) => {
                  click(e.currentTarget);
                }}
              >
                <span></span>
                <span></span>
                <span></span>
              </li>
            </ul>
          </div>
        </div>
      </nav>
    </>
  );
};
export default Nav;
