import { configureStore, createSlice } from "@reduxjs/toolkit";

// detail
const detailInfo = createSlice({
  name: "detailInfo",
  initialState: {},
  reducers: {
    setDetailInfo(state, action) {
      return (state = action.payload);
    },
  },
});
export const { setDetailInfo } = detailInfo.actions;

// cart
const cartData = createSlice({
  name: "cartData",
  initialState: [],
  reducers: {
    addCart(state, action) {
      const foundCartData = state.find(
        (a) => a.id === action.payload.id && a.size === action.payload.size
      );
      if (foundCartData) {
        foundCartData.count++;
      } else {
        state.push(action.payload);
      }
    },
    increaseCount(state, action) {
      const foundCartData = state.find(
        (a) => a.id === action.payload.id && a.size === action.payload.size
      );
      if (foundCartData) {
        foundCartData.count++;
      }
    },
    decreaseCount(state, action) {
      const foundCartData = state.find(
        (a) => a.id === action.payload.id && a.size === action.payload.size
      );
      if (foundCartData && foundCartData.count > 0) {
        foundCartData.count--;
      }
    },
  },
});
export const { addCart, increaseCount, decreaseCount } = cartData.actions;

export default configureStore({
  reducer: {
    detailInfo: detailInfo.reducer,
    cartData: cartData.reducer,
  },
});
