import React, { useEffect, useRef, useState } from "react";
import styles from "./css/Main.module.css";
import Intro from "./../components/Intro";

const Main = ({ page, setPage }) => {
  const [mount, setMount] = useState("");

  useEffect(() => {
    setTimeout(() => {
      setPage("main");
      // 메인 애니메이션 실행
      setMount("mount");
    }, 50); // 50ms 왜??
  }, []);

  useEffect(() => {
    page !== "main" && page !== "" && setUnmountTransition("unmount");
  }, [page]);

  return (
    <>
      <Intro />
      <div className={styles.banner}>
        <div className={styles.banner__center}>
          <h2 className={styles.banner__text}>
            <div><span>we are building a center</span></div>
            <div><span>of excellence for every</span></div>
            <div><span>facet of denim design</span></div>
            <div><span>and production.</span></div>
          </h2>
        </div>
      </div>
    </>
  );
};
export default Main;
