import { Routes,Route,Link,useNavigate,Outlet } from 'react-router-dom';
import { useState } from 'react';
// css
import "./reset.css";
import "./App.css";
// component
import Nav from "./components/Nav/Nav";
// route
import Main from "./router/Main/Main";
import Men from "./router/Men/Men";

function App() {
  const [navItemColor, setNavItemColor] = useState(0);
  const [userColor,setUserColor] = useState("");

  return (
    <div className="App">
      <Nav userColor={userColor}/>
      <Routes>
        <Route path="/" element={<Main />} setUserColor={setUserColor}></Route>
        <Route path="/men" element={<Men />} setUserColor={setUserColor}></Route>
      </Routes>
    </div>
  );
}

export default App;
