import styles from "./css/Cart.module.css";
import { useDispatch, useSelector } from "react-redux";
import { increaseCount, decreaseCount, deleteCart } from "./../store";

const Cart = () => {
  const cartData = useSelector((state) => state.cartData);
  const dispatch = useDispatch();
  console.log(cartData);
  return (
    <div className={styles.cart}>
      <div className={styles.cart__inner}>
        <h2 className={styles.cart__title}>Cart</h2>
        <table className={styles.cart__list}>
          <thead>
            <tr>
              <th>Product</th>
              <th>Quantity</th>
              <th>Price</th>
            </tr>
          </thead>
          <tbody>
            {cartData[0] ? cartData.map((item) => (
              <tr className={styles.product} key={item.id}>
                <td className={styles.product__desc}>
                  <img src={item.url} />
                  <div className={styles.product__option}>
                    <h3>{item.title}</h3>
                    <p>SIZE : {item.size}</p>
                    <span className={styles.delete} 
                    onClick={() => {dispatch(deleteCart(item))}}>DELETE</span>
                  </div>
                </td>
                <td className={styles.product__quantity}>
                  <div className={styles.count}>
                    <small>{item.count}</small>
                    <div className={styles.count__btn}>
                      <div
                        className={styles.count__up}
                        onClick={() => dispatch(increaseCount(item))}
                      ></div>
                      <div
                        className={styles.count__down}
                        onClick={() => dispatch(decreaseCount(item))}
                      ></div>
                    </div>
                  </div>
                </td>
                <td className={styles.product__price}>
                  $ {item.price * item.count}
                </td>
              </tr>
            )) : <strong className={styles.cart__empty}>Your shopping cart is empty!</strong>}
          </tbody>
        </table>
        <ul className={styles.cart__list}>
          <li className={styles.cart_item}>
            <div className={styles.cart_item__thumbnail}></div>
            <div className={styles.cart_item__option}></div>
            <div className={styles.cart_item__Quantity}></div>
            <div className={styles.cart_item__price}></div>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default Cart;
