import React, { useEffect, useRef, useState } from "react";
import styles from "./css/Main.module.css";

const Main = ({ page, setPage }) => {
  const [mountTransition, setMountTransition] = useState("");
  const [unmountTransition, setUnmountTransition] = useState("");
  // 처음 ""
  // main 애니메이션 실행 
  // 1. 처음 main 로드 "main" 
  // 2. 다른 페이지에서 Nav클릭으로 "main"으로 변경

  useEffect(() => {
    if (page !== "main") {
      setPage("main");
    }
    setTimeout(() => {
      // 메인 애니메이션 실행
      setMountTransition("active");
    }, 100)// 50ms 왜??
  }, []);

  useEffect(() => {
    (page !== "main" && page !== "") && setUnmountTransition("unmount");
  }, [page]);

  return (
    <div className={styles.banner}>
      <div
        className={`${styles.banner_cover} ${styles[unmountTransition]}`}
      ></div>
      <div className={styles.banner_left}>
        <div
          className={`${styles.banner_left__img} ${styles[mountTransition]}`}
        >
          <img src={process.env.PUBLIC_URL + "img/banner05.jpg"} />
        </div>
      </div>
    </div>
  );
};
export default Main;
