import React, { useEffect, useRef, useState } from "react";
import styles from "./css/Main.module.css";
import Intro from "./../components/Intro";

const Main = ({ page, setPage }) => {
  const [mountTransition, setMountTransition] = useState("");
  const [unmountTransition, setUnmountTransition] = useState("");

  useEffect(() => {
    setTimeout(() => {
      setPage("main");
      // 메인 애니메이션 실행
      setMountTransition("active");
    }, 50); // 50ms 왜??
  }, []);

  useEffect(() => {
    page !== "main" && page !== "" && setUnmountTransition("unmount");
  }, [page]);

  return (
    <>
      <Intro />
      <div className={styles.banner}>
        <div className={styles.banner__center}>
          <h2 className={styles.banner__text}>
            <div><strong>we are building a center</strong></div>
            <div><strong>of excellence for every</strong></div>
            <div><strong>facet of denim design</strong></div>
            <div><strong>and production.</strong></div>
          </h2>
        </div>
      </div>
    </>
  );
};
export default Main;
